import React, { useState } from 'react';
import Gallery from './components/gallery/Gallery';
import Showcase from './components/showcase/Showcase';
import './index.scss';

const App = () => {
  const [showGallery, setshowGallery] = useState(true)
  const [showShowcase, setshowShowcase] = useState(false)

  const renderGallery = () => {
    setshowGallery(true)
    setshowShowcase(false)
  }

  const renderShowcase = () => {
    setshowShowcase(true)
    setshowGallery(false)
  }
  return (
    <div className="container-fluid">


      <div className="container-lg">
        <div className="header">

        </div>
        <div>
          <MDBBtn onClick={renderGallery} outline rounded color='success' className="m-4">Rich Media Template</MDBBtn>
          <MDBBtn onClick={renderShowcase} outline rounded color='success' className="m-4">Showcase</MDBBtn>
        </div>

        {showGallery ? <Gallery></Gallery> : null}
        {showShowcase ? <Showcase></Showcase> : null}

      </div>
    </div>

  );
}

export default App;
