import React, { useState, useEffect } from 'react';
import { MDBContainer, MDBRow, MDBCol, MDBCardImage, MDBCardBody, MDBCardTitle, MDBCardText, MDBBtn } from 'mdb-react-ui-kit';
import ClipLoader from "react-spinners/ClipLoader";
import './gallery.scss';

const Gallery = () => {
    const [gallery, setGallery] = useState([])
    const [loading, setLoading] = useState(false)
    let [color] = useState("#1E9A4B");

    const getData = async () => {
        const res = await fetch("https://spreadsheets.google.com/feeds/list/1kJl_ioUAK1umhl9oCHF8Oo7u698QdngllHuwerOFpIo/2/public/values?alt=json")
        const data = await res.json()
        setGallery(data.feed.entry)
    }

    useEffect(() => {
        let mounted = true
        if (mounted) {
            getData()
            setLoading(true)
        }
        return function cleanup() {
            mounted = false
        }

    }, [])

    return (
        <MDBContainer>
            {!loading ?
                <ClipLoader color={color} size={180}></ClipLoader>
                :
                <MDBRow>
                    {gallery.map((value, key) => {
                        return <MDBCol key={key} xs="12" sm="6" md="3">
                            <MDBCardImage src={value.gsx$previmg.$t} alt="pfxrichmedia" className="rmThumb" />
                            <MDBCardBody>
                                <MDBCardTitle>{value.gsx$tempname.$t}</MDBCardTitle>
                                <MDBCardText>{value.gsx$desc.$t}</MDBCardText>
                                <a href={value.gsx$demolink.$t} target="_blank" rel="noopener noreferrer"><MDBBtn rounded color="success">嘗試範例</MDBBtn></a>
                                <MDBBtn rounded color="info" data-temp={value.gsx$tempid.$t}>案例參考</MDBBtn>
                            </MDBCardBody>
                        </MDBCol>


                    })}
                </MDBRow>
            }
        </MDBContainer>

    );
}

export default Gallery;
